    <div className="Single">
      <div className="TopSingle">
        <h1 className="titlesingles"> Remova itens </h1>
        <Link className="return" to="/sabores">
          <img src={returnImg} className="" alt="logo" />
        </Link>
        <Link className="return" to="/">
          <img src={HomebtImg} className="" alt="logo" />
        </Link>
      </div>

      <div className="items_main">
        {[...toArray(product.ingredients)].map((ingredient) => (
          <Checkbox.Group
            key={ingredient._id}
            className="itembox"
            onChange={onChange}
            defaultChecked={true}
            checked={true}
          >
            <div>
              <img
                src={
                  process.env.PUBLIC_URL +
                  `/assets/ingredients/${ingredient.image}`
                }
                alt="icon"
              />
            </div>
            <div>
              <h1>{ingredient.ingrdientName}</h1>
            </div>
            <div className="checklabelmain">
              <Checkbox value={ingredient.ingrdientName}></Checkbox>
            </div>
          </Checkbox.Group>
        ))}

