import React, { useContext } from "react";
import { Link } from "react-router-dom";
import PizzaSingle from "../pages/PizzaSingle";
import { ModelContext } from "../../src/Context/ModelContext";

const PizzaCard = ({ sabor, price, pizza }) => {
  const { setCurrentModel } = useContext(ModelContext);
  const openModel = () => setCurrentModel({ name: "PizzaSingle" });
  return (
    // eslint-disable-next-line jsx-a11y/alt-text
    <div>
      <Link onClick={openModel} className="no-underline" to="/pizzasingle">
        <div className="mw5 center bg-white br4 pa3 pa4-ns mv3 ba b--black-10">
          <div className="tc">
            <img
              className="br-100 h4 w4 dib pa"
              alt={pizza.image}
              src={
                process.env.PUBLIC_URL +
                `/assets/${pizza.category}/${pizza.image}`
              }
            />
            <h1 className="f3 mb2" style={{ color: "#000" }}>
              {sabor}
            </h1>
            <h2 className="f4 fw4" style={{ color: "#FF9100" }}>
              {price} - R$
            </h2>
          </div>
        </div>
      </Link>
      <PizzaSingle pizza={pizza} />
    </div>
  );
};

export default PizzaCard;
