import React, { createContext, useState } from "react";

export const OrderListContext = createContext({
  orderType: "",

  SetOrderListContext: (info) => {},
});

const OrderListContextProvider = ({ children }) => {
  const OrderListState = {
    orderType: "",

    SetOrderListContext: (info) =>
      SetOrderList((prevState) => ({
        ...prevState,
        [Object.keys(info)]: Object.values(info)[0],
      })),
  };

  const [orderList, SetOrderList] = useState(OrderListState);
  return (
    <OrderListContext.Provider value={orderList}>
      {children}
    </OrderListContext.Provider>
  );
};

export default OrderListContextProvider;
