import React, { useState } from "react";
import { Link } from "react-router-dom";

const PizzaCard = ({ sabor, price, category, image, ingredients, pizza }) => {
  const [precoPizzaItem, setPrecoPizzaItem] = useState();

  function checkUsePrecoPizzaConfig() {
    fetch("http://localhost:4000/api/config/session")
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
        if (data.precoPizza === 1) {
          document.getElementsByTagName("precopizza")("h2").style.display =
            "none";
        } else {
          document.getElementsByTagName("precopizza").style.display = "block";
        }
      });
  }
  checkUsePrecoPizzaConfig();

  return (
    <Link className="no-underline" to={"/pizzasingle/" + pizza._id}>
      <div className="center bg-white b--black-10">
        <div className="tc">
          <img
            className="br-100 h4 w4 dib pa"
            alt={image}
            src={process.env.PUBLIC_URL + `/assets/${category}/${image}`}
          />
          <h1 style={{ color: "#000" }}>{sabor}</h1>
          <h2 className="precopizza" style={{ color: "#FF9100" }}>
            {price} - R$
          </h2>
        </div>
      </div>
    </Link>
  );
};

export default PizzaCard;
