import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { detailsProduct } from "../../actions/productActions";
import { useSelector, useDispatch } from "react-redux";
import returnImg from "../../assets/return.png";
import HomebtImg from "../../assets/homebotton.png";
import "./PizzaSingle.css";

const PizzaSingle = (props) => {
  const [qty, setQty] = useState(1);
  const productDetails = useSelector((state) => state.productDetails);
  const { product, loading, error } = productDetails;

  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(detailsProduct(props.match.params.id));
    return () => {
      //
    };
  }, []);

  const handleAddtoCart = () => {
    props.history.push("/check" + props.match.params.id + "?qty=" + qty);
  };

  return loading ? (
    <div>Loading...</div>
  ) : error ? (
    <div>{error}</div>
  ) : (
    <div className="Single">
      <div className="TopSingle">
        <h1 className="titlesingles"> Remova itens </h1>
        <Link className="return" to="/sabores">
          <img src={returnImg} className="" alt="logo" />
        </Link>
        <Link className="return" to="/">
          <img src={HomebtImg} className="" alt="logo" />
        </Link>
      </div>

      <div className="items_main">
        {[...Array(product.ingredients)].map((ingredient, i) => {
          return (
            <div className="itembox">
              <span>
                <img src={ingredient.image} alt="icon" />
              </span>
              <input
                onChange={(e) => {
                  setQty(e.target.value);
                }}
                type="checkbox"
                value={ingredient.ingrdientName}
              />
              <label for="vehicle1"> {ingredient.image}</label>
            </div>
          );
        })}
      </div>

      <div className="items_footer">
        <h1>{product.sabor}</h1>
        <h2>{product.price} $R</h2>
        <Link className="validItems" onClick={handleAddtoCart} to="/check">
          <h1>ESCOLHER</h1>
        </Link>
      </div>
    </div>
  );
};

export default PizzaSingle;
