import React, { useState } from "react";
import { Link } from "react-router-dom";
import HomebtImg from "../../assets/homebotton.png";
//import Subtract from "../../assets/Subtract.png";
import OneSubtract from "../../assets/OneSubtract.png";
import Pizzabanana1 from "../../assets/pizzabanana1.png";
import Plus from "../../assets/Plus.png";
import delect from "../../assets/delect.png";
import "./Check.css";
import { ModelContext } from "../../Context/ModelContext";

const Check = ({ pizza }) => {
  const [count, setCount] = useState(1);

  const { setCurrentModel } = useContext(ModelContext);
  const openModel = () => setCurrentModel({ name: "CheckModal" });

  return (
    <div className="Check">
      <div className="Check_main">
        <div className="TopCheck">
          <h1 className="titlechecks"> Seu pedido </h1>
          <Link className="return" to="/">
            <img src={HomebtImg} className="" alt="logo" />
          </Link>
        </div>
      </div>

      <div className="check_main">
        <div className="onepizzabox">
          <div className="pizzanameandprice">
            <img src={Pizzabanana1} alt="Pizza" />
            <div className="pizzanameandpricemain">
              <h2>BANANA</h2>
              <h3>24 $R</h3>
            </div>
          </div>

          <p className="pizzaItemsSelect">
            Banana ,Açúcar ,Muçarela, Banana , Açúcar ,Muçarela Banana ,Açúcar
            ,Muçarela, Banana , Açúcar ,Muçarela
          </p>

          <button
            onClick={() => setCount(count > 1 ? count - 1 : 1)}
            type="button"
            className="btn btn-secondary"
          >
            <img src={OneSubtract} alt="" />
          </button>
          <h1 className="TotalPizzaSelects">{count}</h1>

          <button
            onClick={() => setCount(count + 1)}
            type="button"
            className="btn btn-secondary"
          >
            <img src={Plus} alt="" />
          </button>
          <button>
            <img src={delect} alt="" />
          </button>
        </div>
      </div>

      <div className="check_Reorder">
        <Link className="Reorder" to="/select">
          <h1>ADICIONAR OUTRA PIZZA</h1>
        </Link>
        <Link onClick={openModel} className="Checkvalid">
          <h1>FINALIZAR PEDIDO</h1>
        </Link>
      </div>

      <div className="check_footer">
        <h1 className="TotalPrice">VALOR TOTAL : 24 $R</h1>

        <h1 className="TotalPizza">PIZZA : {count} </h1>
      </div>
    </div>
  );
};

export default Check;
