import React from "react";
import { Link } from "react-router-dom";

const PizzaCard = ({ sabor, price, pizza }) => {
  return (
    // eslint-disable-next-line jsx-a11y/alt-text
    <Link className="no-underline" to="/pizzasingle">
      <div className="mw5 center bg-white br4 pa3 pa4-ns mv3 ba b--black-10">
        <div className="tc">
          <img
            className="br-100 h4 w4 dib pa"
            alt={pizza.image}
            src={
              process.env.PUBLIC_URL +
              `/assets/${pizza.category}/${pizza.image}`
            }
          />
          <h1 className="f3 mb2" style={{ color: "#000" }}>
            {sabor}
          </h1>
          <h2 className="f4 fw4" style={{ color: "#FF9100" }}>
            {price} - R$
          </h2>
          <p>{pizza.ingredient}</p>
        </div>
      </div>
    </Link>
  );
};

export default PizzaCard;
