import React, { useContext } from "react";
import { Link } from "react-router-dom";

import { ModelContext } from "../../src/Context/ModelContext";

const PizzaCard = ({ sabor, price, category, image, ingredient, pizza }) => {
  const { setCurrentModel } = useContext(ModelContext);
  return (
    // eslint-disable-next-line jsx-a11y/alt-text

    <Link className="no-underline" to={"/pizzasingle/" + pizza._id}>
      <div className="center bg-white b--black-10">
        <div className="tc">
          <img
            className="br-100 h4 w4 dib pa"
            alt={image}
            src={process.env.PUBLIC_URL + `/assets/${category}/${image}`}
          />
          <h1 style={{ color: "#000" }}>{sabor}</h1>
          <h2 style={{ color: "#FF9100" }}>{price} - R$</h2>
        </div>
      </div>
    </Link>
  );
};

export default PizzaCard;
