import React from 'react';
import PizzaCard from './PizzaCard'


const PizzaCardList = ({pizzas}) => {
	const cardComponent = pizzas.map((pizza,i) => {
    return (
      <PizzaCard 
        price={pizzas[i].price}
        name={pizzas[i].name}
        unit={pizzas[i].unit}
        pizza={pizza} />
    )
  })
  return (
    <div className="AllSabores">
      {cardComponent}
    </div>
  )
};

export default PizzaCardList;
