import React, { useState, useEffect, useContext } from "react";
import { Link } from "react-router-dom";
import { detailsProduct } from "../../actions/productActions";
import { addtoCart } from "../../actions/cartAction";
import { useSelector, useDispatch } from "react-redux";
import returnImg from "../../assets/return.png";
import HomebtImg from "../../assets/homebotton.png";
import { CheckIngredientContext } from "../../Context/IngredientContext";
import "./PizzaSingle.css";
import { toArray } from "antd/lib/form/util";
import { Checkbox } from "antd";
//import "antd/dist/antd.css";

const PizzaSingle = (props) => {
  //const checkIngredient = useContext(CheckIngredientContext);
  const value = useContext(CheckIngredientContext);
  const { checked, SetCheckIngredientContext } = value;
  const productDetails = useSelector((state) => state.productDetails);
  const { product, loading, error } = productDetails;
  const [precoPizzaItem, setPrecoPizzaItem] = useState();

  const [checkValue, SetcheckValue] = useState([]);

  const [qty, setQty] = useState(1);
  const quantyDefaut = () => {
    return setQty + 1;
  };
  quantyDefaut();

  const [ing, setIng] = useState({});
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(detailsProduct(props.match.params.id));
    return () => {
      //
    };
  }, [props.match.params.id, dispatch]);

  const handleAddtoCart = () => {
    props.history.push("/check/" + props.match.params.id + "?qty=" + qty);
  };

  const add = (product, quantity, ing) => {
    props.history.push("/check/" + props.match.params.id);
    dispatch(addtoCart(product, quantity, ing));
  };

  function checkUsePrecoPizzaConfig() {
    fetch("http://localhost:4000/api/config/session")
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
        if (data.precoPizza === 0) {
          setPrecoPizzaItem("precoPizza");
        }
      });
  }
  checkUsePrecoPizzaConfig();

  function onChange(checkedValues) {
    SetcheckValue(checkedValues);
    console.log(checkValue);
  }

  return loading ? (
    <div>Loading...</div>
  ) : error ? (
    <div>{error}</div>
  ) : (
    <div className="Single">
      <div className="TopSingle">
        <h1 className="titlesingles"> Remova itens </h1>
        <Link className="return" to="/sabores">
          <img src={returnImg} className="" alt="logo" />
        </Link>
        <Link className="return" to="/">
          <img src={HomebtImg} className="" alt="logo" />
        </Link>
      </div>

      <div className="items_main">
        {[...toArray(product.ingredients)].map((ingredient) => (
          <Checkbox.Group
            key={ingredient._id}
            className="itembox"
            onChange={onChange}
          >
            <div>
              <img
                src={
                  process.env.PUBLIC_URL +
                  `/assets/ingredients/${ingredient.image}`
                }
                alt="icon"
              />
            </div>
            <div>
              <h1>{ingredient.ingrdientName}</h1>
            </div>
            <div className="checklabelmain">
              <Checkbox value={ingredient.ingrdientName}></Checkbox>
            </div>
          </Checkbox.Group>
        ))}
      </div>

      <div className="items_footer">
        <h1>{product.sabor}</h1>
        <h2 className={precoPizzaItem}>{product.price} $R</h2>
        <Link
          to={"/check/" + props.match.params.id + "?qty=" + qty}
          className="validItems"
          onclick={() => add(product, qty, ing)}
        >
          <h1>ESCOLHER</h1>
        </Link>
      </div>
    </div>
  );
};

export default PizzaSingle;
