import React from 'react';
import { Link } from 'react-router-dom';

const PizzaCard = props => {
  const { item } = props
  return (

     <Link className="no-underline" to="item={item}">
        <div className="mw5 center bg-white br4 pa3 pa4-ns mv3 ba b--black-10">
      <div className="tc">
        <img className="br-100 h4 w4 dib pa" src={process.env.PUBLIC_URL + `/assets/pizza1`}

          />
        <h1 className="f3 mb2" style={{color:'#000'}}>{item.name}</h1>
        <h2 className="f4 fw4" style={{color:'#FF9100'}}>{item.price} / {item.unit}</h2>
      </div>  
     </div>
     </Link>
  );
};

const PizzaCardList = (props) => {
  const { data, category } = props;
  return (
  	<div className="AllSabores">
        data.map((item) => <PizzaCard item={item} />)
    </div>
  );
};

export default PizzaCardList;
