import React from 'react';
import PizzaCard from './PizzaCard'


const PizzaCardList = ({pizzas}) => {
	const cardComponent = pizzas.map((pizza) => {
    return (
      <PizzaCard 
        price={pizza.price}
        name={pizza.name}
        unit={pizzas[pizza].unit} />
    )
  })
  return (
    <div className="AllSabores">
      {cardComponent}
    </div>
  )
};

export default PizzaCardList;
