import React, { Fragment, useState, useEffect, useMemo } from "react";
import { Link } from "react-router-dom";
import PizzaCardList from "../../components/PizzaCardList";
import { listProducts } from "../../actions/productActions";
//import Pagination from "../../components/Pagination";
import { Pagination } from "antd";
import "tachyons";
import "./Sabores.css";
import returnImg from "../../assets/return.png";
import HomebtImg from "../../assets/homebotton.png";
import { useSelector, useDispatch } from "react-redux";
//import axios from 'axios';

const Sabores = (props) => {
  const category = props.match.params.id ? props.match.params.id : "";
  const productList = useSelector((state) => state.productList);
  const { products, loading, error } = productList;

  const dispatch = useDispatch();

  const [productsPerPage, setProductsPerPage] = useState(8);
  const [currentPage, setCurrentPage] = useState(1);

  useEffect(() => {
    dispatch(listProducts(category));
  }, [category, dispatch, currentPage, productsPerPage]);

  console.log(products);
  // Get current posts
  const indexOfLastProducts = currentPage * productsPerPage;
  const indexOfFirstProducts = indexOfLastProducts - productsPerPage;
  const currentProducts = products.slice(
    indexOfFirstProducts,
    indexOfLastProducts
  );

  // Change page
  function itemRender(currentPage, type, originalElement) {
    if (type === "prev") {
      return <a href>Previous</a>;
    }
    if (type === "next") {
      return <a href>Next</a>;
    }
    return originalElement;
  }

  return loading ? (
    <div>Loading...</div>
  ) : error ? (
    <div>{error}</div>
  ) : (
    <Fragment>
      <div className="Sabores">
        <div className="sabores_main">
          <div className="TopSabores">
            <h1 className="titlesabores"> Escolha sua pizza </h1>
            <Link className="return" to="/tipo">
              <img src={returnImg} className="" alt="logo" />
            </Link>
            <Link className="return" to="/">
              <img src={HomebtImg} className="" alt="logo" />
            </Link>
          </div>
          {category}
          <PizzaCardList pizzas={currentProducts} category={category} />

          <div className="saborespagination">
            <Pagination
              productsPerPage={productsPerPage}
              onShowSizeChange={setProductsPerPage}
              // total={products.length}
              defaultCurrent={currentPage}
              itemRender={itemRender}
              onChange={setCurrentPage}
            />
          </div>
        </div>
      </div>
    </Fragment>
  );
};

export default Sabores;
