        {[...toArray(product.ingredients)].map((ingredient) => (
          <div key={ingredient._id} className="itembox">
            <span>
              <img src={ingredient.image} alt="icon" />
            </span>
            <h1> {ingredient.ingrdientName}</h1>
            <input
              onChange={(e) => {
                setIng(e.target.value);
              }}
              type="checkbox"
              value={ingredient.ingrdientName}
            />
          </div>
        ))}

