import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import returnImg from "../../assets/return.png";
import HomebtImg from "../../assets/homebotton.png";
import "./PizzaSingle.css";
import { addtoCart } from "../../actions/actions";

const PizzaSingle = ({ pizza }) => {
  const pizzas = useSelector((state) => state.pizzas);

  const [qty] = useState(1);
  const dispatch = useDispatch();

  const add = (pizza, quantity) => {
    dispatch(addtoCart(pizza, quantity));
  };

  const ingredients = pizzas.map((pizza) => {
    console.log(pizza);
    return (
      <div className="itembox">
        <span>
          <img src="" alt="icon" />
        </span>
        <h1>{pizza.ingredient}</h1>
        <span>
          <input type="checkbox" />
        </span>
      </div>
    );
  });

  return (
    <div className="Single">
      <div className="TopSingle">
        <h1 className="titlesingles"> Remova itens </h1>
        <Link className="return" to="/sabores">
          <img src={returnImg} className="" alt="logo" />
        </Link>
        <Link className="return" to="/">
          <img src={HomebtImg} className="" alt="logo" />
        </Link>
      </div>

      <div className="items_main">{ingredients}</div>

      <div className="items_footer">
        <h1>pizzas.name</h1>
        <h2>pizzas.price $R</h2>
        <Link
          onclick={() => add(pizza, qty)}
          className="validItems"
          to="/check"
        >
          <h1>ESCOLHER</h1>
        </Link>
      </div>
    </div>
  );
};

export default PizzaSingle;
