import React,{Fragment,useRef} from "react";
import PizzaCard from "./PizzaCard";


const PizzaPreview = ({products})=>{
  {products.map(({id,...otherCollectionProps})=>(
    <PizzaCardList key={id} {...otherCollectionProps}/>
  ))}

}


const PizzaCardList = ({products}) => {
  const inputRef = useRef(null);
  return(
    <Fragment>
    {products.map(({ id, ...otherItemProps }) => (
      <PizzaCard ref={inputRef} key={id} {...otherItemProps} />
    ))}
  </Fragment>
  )
};

export default PizzaCardList;
