      <PizzaCard
        key={pizza._id}
        price={pizza.price}
        pizza={pizza}
        sabor={pizza.sabor}
        ingredient={pizza.ingredient}
        category={category.category}
        image={pizza.image}
      />
    );
  });
  return <div className="AllSabores">{cardComponent}</div>;
};

export default PizzaCardList;
